import React from "react"
import TodoItem from "./TodoItem"
import todosData from "./todosData"

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            todos: todosData
        }

        const [todos, setTodos] =
        useState(todosData);
        this.setTodos = setTodos;
        this.todos = todos;

        this.handleChange = this.handleChange.bind(this)
        this.addItem = this.addItem.bind(this)
        this.deleteItem = this.deleteItem.bind(this)
    }

    handleChange(id) {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed
                }
                return todo
            })
            return {
                todos: updatedTodos
            }
        })
    }

    addItem() {
      const newItem = this.state.todos;
      if(newItem.todos !==""){

        this.setState({
          todos: todosData
        })
      }
    }

    deleteItem(id){
      //debugger
    const filteredItems= this.state.todos.filter(item =>
      item.id!==id);
    this.setState({
      todos: filteredItems
    })
  }

    render() {
        const todoItems = this.state.todos.map
        (item => <TodoItem key={item.id}
        item={item} handleChange={this.handleChange}
        deleteItem={this.deleteItem}
        />)

        return (
            <div className="todo-list">
                {todoItems}

            <button onClick={this.addTodo}>Add todo</button>
            </div>
        )
    }
}

class addingToTodoData {
  constructor(container) {
    this.container = container;
    this.todoList = [
      new Todo(4, "make a lunch box", false),
    ];
    this.filteredProducts = this.todoList;
    this.renderProductList();
  }};



export default App
