import React, {useState, useEffect} from "react"

function timer() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCount(prevCount => prevCount + 1)
    }, 1000)
    return () => clearInterval(intervalId)
  }, [])

  return (
    <div>
      <p>{count}</p>
    </div>
  )
}

export default timer
